INSERT INTO `wp_newsletter_emails` VALUES (1,'<!DOCTYPE html><html><head><title>Newsletter</title><meta charset=\"utf-8\"><meta name=\"viewport\" content=\"width=device-width, initial-scale=1\"><meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\" /><style type=\"text/css\">/* CLIENT-SPECIFIC STYLES */\r\n#outlook a{padding:0;} /* Force Outlook to provide a \"view in browser\" message */\r\n.ReadMsgBody{width:100%;} .ExternalClass{width:100%;} /* Force Hotmail to display emails at full width */\r\n.ExternalClass, .ExternalClass p, .ExternalClass span, .ExternalClass font, .ExternalClass td, .ExternalClass div {line-height: 100%;} /* Force Hotmail to display normal line spacing */\r\nbody, table, td, a{-webkit-text-size-adjust:100%; -ms-text-size-adjust:100%;} /* Prevent WebKit and Windows mobile changing default text sizes */\r\ntable, td{mso-table-lspace:0pt; mso-table-rspace:0pt;} /* Remove spacing between tables in Outlook 2007 and up */\r\nimg{-ms-interpolation-mode:bicubic;} /* Allow smoother rendering of resized image in Internet Explorer */\r\n\r\n/* RESET STYLES */\r\nbody{margin:0; padding:0;}\r\nimg{border:0; height:auto; line-height:100%; outline:none; text-decoration:none;}\r\ntable{border-collapse:collapse !important;}\r\nbody{height:100% !important; margin:0; padding:0; width:100% !important;}\r\n\r\n/* MOBILE STYLES */\r\n@media screen and (max-width: 525px) {\r\n\r\n    /* ALLOWS FOR FLUID TABLES */\r\n    table[class=\"wrapper\"]{\r\n        width:100% !important;\r\n    }\r\n\r\n    /* ADJUSTS LAYOUT OF LOGO IMAGE */\r\n    td[class=\"logo\"]{\r\n        text-align: left;\r\n        padding: 20px 0 20px 0 !important;\r\n    }\r\n\r\n    td[class=\"logo\"] img{\r\n        margin:0 auto!important;\r\n    }\r\n\r\n    /* USE THESE CLASSES TO HIDE CONTENT ON MOBILE */\r\n    td[class=\"mobile-hide\"]{\r\n        display:none;}\r\n\r\n    img[class=\"mobile-hide\"]{\r\n        display: none !important;\r\n    }\r\n\r\n    img[class=\"img-max\"]{\r\n        max-width: 100% !important;\r\n        width: 100% !important;\r\n        height:auto !important;\r\n    }\r\n\r\n    /* FULL-WIDTH TABLES */\r\n    table[class=\"responsive-table\"]{\r\n        width:100%!important;\r\n    }\r\n\r\n    /* UTILITY CLASSES FOR ADJUSTING PADDING ON MOBILE */\r\n    td[class=\"padding\"]{\r\n        padding: 10px 5% 15px 5% !important;\r\n    }\r\n\r\n    td[class=\"padding-copy\"]{\r\n        padding: 10px 5% 10px 5% !important;\r\n        text-align: center;\r\n    }\r\n\r\n    td[class=\"padding-meta\"]{\r\n        padding: 30px 5% 0px 5% !important;\r\n        text-align: center;\r\n    }\r\n\r\n    td[class=\"no-pad\"]{\r\n        padding: 0 0 20px 0 !important;\r\n    }\r\n\r\n    td[class=\"no-padding\"]{\r\n        padding: 0 !important;\r\n    }\r\n\r\n    td[class=\"section-padding\"]{\r\n        padding: 50px 15px 50px 15px !important;\r\n    }\r\n\r\n    td[class=\"section-padding-bottom-image\"]{\r\n        padding: 50px 15px 0 15px !important;\r\n    }\r\n\r\n    /* ADJUST BUTTONS ON MOBILE */\r\n    td[class=\"mobile-wrapper\"]{\r\n        padding: 10px 5% 15px 5% !important;\r\n    }\r\n\r\n    table[class=\"mobile-button-container\"]{\r\n        margin:0 auto;\r\n        width:100% !important;\r\n    }\r\n\r\n    a[class=\"mobile-button\"]{\r\n        width:80% !important;\r\n        padding: 15px !important;\r\n        border: 0 !important;\r\n        font-size: 16px !important;\r\n    }\r\n\r\n}</style><link href=\"http://fonts.googleapis.com/css?family=Source+Sans+Pro:200,300,400,600,700,900,200italic,300italic,400italic,600italic,700italic,900italic\" rel=\"stylesheet\" type=\"text/css\"><link rel=\"stylesheet\" href=\"http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/css/font-awesome.min.css\"></head><body style=\"margin: 0; padding: 0;\">\r\n\r\n            <!-- HEADER -->\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" data-id=\"header-01\" class=\"tnpc-row ui-sortable-handle\">\